import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import PokemonDetail from './PokemonDetail';

const PokemonDetailPage = ({ match }) => {
  const {
    params: { pokemonId },
  } = match;
  const [pokemon, setPokemon] = useState(null);
  const [error, setError] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const API_BASE_URL = `https://pokeapi.co/api/v2/pokemon/`;
    const fetchPokemon = async () => {
      setLoading(true);
      setError(false);
      try {
        const result = await axios.get(`${API_BASE_URL}/${pokemonId}`);
        setPokemon(result.data);
      } catch (error) {
        setError(true);
      }
      setLoading(false);
    };
    // Call the API
    fetchPokemon();
  }, [pokemonId]);

  return (
    <>
    <div><h3>Pokemon Details</h3>
    <div className="card text-center mx-auto" style={{"maxWidth" : "18rem"}}>
        <div className="card-header"><b>{pokemon.name}</b></div>
        <div className="card-body">           
          {/* <h6 className="card-subtitle mb-2 text-muted">Height: {pokemon.height}</h6>  
          <h6 className="card-subtitle mb-2 text-muted">Weight: {pokemon.weight}</h6>  
          <img src={pokemon.sprites['front_default']} />
          <img src={pokemon.sprites['back_default']} />  */}

          {/* <button type="button" className="btn btn-success btn-block" key="more-button" id="more-button">Detail</button>                       */}
        </div>
      </div>
      <Link to={`/`}>Go back to pokemon list</Link>
      </div>
    </>
  );
};

export default PokemonDetailPage;
